import React, {useState, useEffect} from 'react';

import {
  BrowserRouter,
  Route,
  Routes,
} from 'react-router-dom';

import {
  createTheme,
  makeStyles,
  ThemeProvider} from '@material-ui/core';
import { purple } from '@mui/material/colors';
import NewsContainer from './Components/NewsContainer';
import AlertContainer from './Components/AlertContainer';
import Auth from './Components/auth/Auth';
import Calendar from './Components/Calendar';
import Layout from './Components/Layout';
import Create from './Components/pages/Create';
import Notes from './Components/pages/Notes';

const theme = createTheme({
  palette: {
    primary: {
      main: '#fefefe'
    },
    secondary: purple
  },

  typography: {
    fontFamily: 'Quicksand',

    fontWightLight: 400,

    fontWightRegular: 500,

    fontWightMedium: 600,

    fontWightBold: 700
  },
});


const useStyles = makeStyles({
  page: 
  {
    background: '#f9f9f9',
    width: '100%' 
  },

  field: {
    marginTop: 20,
    marginBotton: 20,
    display: 'block'
  }
});



const App = () => 
{
  const [category, setCategory] = useState('');
  const [news, setNews] = useState([]);
  const [query, setQuery] = useState('');
  const [error, setError] = useState(false);
  const [loading, setLoading] = useState({});
  useEffect(() => {
      if (category === '') return;
      setLoading({
          status: true,
          maxPercent: 80,
      });
      const getData = async () => {
          const url = !query
              ? `https://api.currentsapi.services/v1/latest-news?apiKey=Iv0ingCnOq7YDglaNcaU8Lnz8iHVNygfdHIyH_aVypU0J_WT&category=${category}`
              : `https://api.currentsapi.services/v1/search?keywords=${query}&apiKey=Iv0ingCnOq7YDglaNcaU8Lnz8iHVNygfdHIyH_aVypU0J_WT`;

          // const url = 'https://newsapi.org/v2/everything?q=tesla&from=2021-12-24&sortBy=publishedAt&apiKey=9d927e430d514caaa28eb3e1622bf187';

          const response = await fetch(url);
          const data = await response.json();
          console.log(data);
          setLoading({
              status: true,
              maxPercent: 100,
          });
          if (data.news.length) {
              setError(false);
          } else {
              setError(true);
              setLoading({
                  status: false,
                  maxPercent: 0,
              });
          }

          setNews(data.news);
      };
      setNews([]);
      getData();
  }, [category, query]);
  const classes = useStyles();
  return (
    <div>
     <ThemeProvider theme={theme} >
        <BrowserRouter>
        <Layout>
          <Routes>
                <Route path="/" element={<Auth/>}/>
                <Route path="/Create" element={<Create/>}/>
                <Route path="/Notes" element={<Notes/>}/>
                <Route path="/Calendar" element={<Calendar/>}/>
                <Route path="/news" element={
                <NewsContainer news={news} />
            // ) : (
            //     <AlertContainer
            //         query={query}
            //         type={'error'}
            //         message={`There aren't results for "${query}" â€” `}
            //         messageStrong={'Try more general words.'}
            //     />
            }/>
          </Routes>
        </Layout>
        </BrowserRouter>
    </ThemeProvider>   
    </div>
  )
}

export default App;
