import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import CardActionArea from '@mui/material/CardActionArea';

import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import ShareButton from './ShareButton';

const useStyles = makeStyles({
    root: {
        maxWidth: 340,
    },
    media: {
        height: 180,
    },
});

const News = ({ newsData: { url, image, title, description, author } }) => {
    const classes = useStyles();
    const openNews = (url) =>
        setTimeout(() => {
            window.open(url);
        }, 150);

    return (
        <Card className={classes.root}>
            <CardActionArea onClick={() => openNews(url)}>
                <CardMedia className={classes.media} image={image} title={title} />
                <CardContent>
                    <Typography gutterBottom variant="h5" component="h2">
                        {title}
                    </Typography>
                    <Typography variant="body2" color="textSecondary" component="p">
                        {description}
                    </Typography>
                </CardContent>
            </CardActionArea>
            <CardActions>
                {/* <ShareButton url={url} /> */}
                {/* <Button size="small" color="primary" onClick={() => openNews(url)}>
                    Read More
                </Button> */}
                <Button color="secondary" onClick={() => openNews(url)}>Read More</Button>
                <Button size="small" color="primary" onClick={handleClick}>
                Share
            </Button>
            <Menu
                id="simple-menu"
                anchorEl={open}
                keepMounted
                open={Boolean(open)}
                onClose={handleClose}
            >
                <MenuItem onClick={() => handleClose(urls.facebook)}>Facebook</MenuItem>
                <MenuItem onClick={() => handleClose(urls.twitter)}>Twitter</MenuItem>
                <MenuItem onClick={() => handleClose(urls.whatsapp)}>Whatsapp</MenuItem>
            </Menu>
            </CardActions>
        </Card>
    );
};

export default News;
