import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Alert, AlertTitle } from '@material-ui/lab';
import Container from '@material-ui/core/Container';
// import { capitalize } from './helpers';

const useStyles = makeStyles(() => ({
    root: {
        width: '60%',
        marginTop: 25,
    },
}));

function getPageCategories() {
    // theses categories come from https://api.currentsapi.services/v1/available/categories
    return [
        'general',
        'business',
        'programming',
        'entertainment',
        'health',
        'science',
        'sports',
        'technology',
        'gadgets',
        'game',
        'security',
    ];
}

function capitalize(string) {
    return string[0].toUpperCase() + string.slice(1);
}

const AlertContainer = ({ query, type, message, messageStrong }) => {
    const classes = useStyles();

    return (
        <Container className={classes.root} justify={'center'}>
            <Alert severity={type}>
                <AlertTitle>{capitalize(type)}</AlertTitle>
                {message}
                <strong>{messageStrong}</strong>
            </Alert>
        </Container>
    );
};

export default AlertContainer;
