import React, { useEffect, useState} from 'react';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import { Container } from '@material-ui/core';
import NoteCard from './component_note/NoteCard.js';
import { makeStyles } from '@material-ui/core';
import TextField from '@mui/material/TextField';

const axios = require('axios');

const useStyles = makeStyles({
    page: 
    {
      background: '#f9f9f9',
      width: '100%',
      padding:20 
    },
    
    field: {
      marginTop: 20,
      marginBotton: 20,
      display: 'block'
    }
});

const Search = styled('div')(({ theme }) => ({
  position: 'relative',
  borderRadius: theme.shape.borderRadius,
  backgroundColor: alpha(theme.palette.common.white, 0.15),
  '&:hover': {
    backgroundColor: alpha(theme.palette.common.white, 0.25),
  },
  marginRight: theme.spacing(2),
  marginLeft: 0,
  width: '100%',
  [theme.breakpoints.up('sm')]: {
    marginLeft: theme.spacing(3),
    width: 'auto',
  },
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
  padding: theme.spacing(0, 2),
  height: '100%',
  position: 'absolute',
  pointerEvents: 'none',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: 'inherit',
  '& .MuiInputBase-input': {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('md')]: {
      width: '20ch',
    },
  },
}));
  
export default function Notes() {

    const classes = useStyles();

    const [notes, setNotes] = useState([]);

     //     set search query to empty string
     const [q, setQ] = useState("");
     //     set search parameters
     //     we only what to search countries by capital and name
     //     this list can be longer if you want
     //     you can search countries even by their population
     // just add it to this array
     const [searchParam] = useState(["category"]);


     const search = (items) => {
      return items.filter((item) => {
        return searchParam.some((newItem) => {
            return (
                item[newItem]
                    .toString()
                    .toLowerCase()
                    .indexOf(q.toLowerCase()) > -1
              );
          });
      });
    }

    const cancelSearch = () => {
      setQ("");
    };
  
    useEffect(() => {
      axios.get('http://localhost:4000/notes')
        // .then(res => res.json())
        .then(data => 
          { setNotes(data.data.notes);
              console.log(data); }
        )
    }, [])
  
    const handleDelete = async (_id) => {
      console.log(_id);

      await fetch('http://localhost:4000/notes/' + _id, {
        method: 'DELETE'
      }).then(res => console.log(res)).catch(err => console.log(err));

      console.log(notes);

      const newNotes = notes.filter(note => note._id != _id)
      setNotes(newNotes)
    }

    const handleUpdate = (_id) => 
    {
      console.log(_id);
    }
//xs={12}  lg={4}
    return(
      <Container className={classes.page}>

          <Search>
            <SearchIconWrapper>
              <SearchIcon />
            </SearchIconWrapper>
            <StyledInputBase
              placeholder="Search…"
              inputProps={{ 'aria-label': 'search' }}
              value={q}
              onChange={(e) => setQ(e.target.value)}
            />
          </Search>
            <TextField
              variant="standard"
              value={q}
              onChange={(e) => setQ(e.target.value)}
              placeholder="Search category…          "
              // InputProps={<SearchIcon/>}
                //   <IconButton
                //     title="Clear"
                //     aria-label="Clear"
                //     size="small"
                //     // style={{ visibility: props.value ? 'visible' : 'hidden' }}
                //     style="visible"
                //     // onClick={props.clearSearch}
                //   >
                //     <ClearIcon fontSize="small" />
                //   </IconButton>
                //   }
              sx={{
                width: {
                  xs: 1,
                  sm: 'auto'
                  // lg: 'auto'
                },
                m: (theme) => theme.spacing(1, 0.5, 1.5),
                '& .MuiSvgIcon-root': {
                  mr: 0.5,
                },
                '& .MuiInput-underline:before': {
                  borderBottom: 1,
                  borderColor: 'divider',
                },
              }}
            />

        <Grid container spacing={3}>
          {search(notes).map(note => (
            <Grid item xs={12} md={6} lg={4} key={note._id}>
              <NoteCard note={note} handleDelete={() => handleDelete(note._id)} handleUpdate={handleUpdate}/>
            </Grid>
          ))}
        </Grid>
      </Container>
    )
}
