import React, {useState} from 'react';
import Typography from '@mui/material/Typography';
import Button from '@material-ui/core/Button';
import KeyboardArrowRightIcon from '@material-ui/icons/KeyboardArrowRight';
import { FormControlLabel } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import Container from '@material-ui/core/Container';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import {useNavigate} from 'react-router-dom';



export default function Create({classes}) {


  const [title, setTitle] = useState('');
  const [details, setDetails] = useState('');
  const [titleError, setTitleError] = useState(false);
  const [detailsError, setDetailsError] = useState(false);
  const [category, setCategory] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => 
  { 
    e.preventDefault() 
    setTitleError(false);
    setDetailsError(false);
    
    
    if(title === '')
    {
        setTitleError(true);
    }

    if(details === '')
    {
        setDetailsError(true);
    }

    if(title && details)
    {
        fetch('https://localhost:4000/notes', {
          method: 'POST',
          headers: {"Content-type": "application/json"},
          body: JSON.stringify({title, details, category}).
            then(navigate.push('/'))
        });
    }
  }

  return (
    <Container>

        <Typography color="textSecondary" variant = "h6" component="h2" gutterBottom>
          Create a New Note
        </Typography>


        <form noValidate autoComplete="off" onSubmit={handleSubmit}>
          <TextField 
            onChange={(e) => setTitle(e.target.value)}
            label="Note title"
            variant="outlined"
            color="secondary"
            fullWidth
            required
            className={classes.field}
            error={titleError}
          />
          <TextField 
            onChange={(e) => setDetails(e.target.value)}
            label="Detailed"
            variant="outlined"
            color="secondary"
            multiline
            rows={4}
            fullWidth
            required
            className={classes.field}
            error={detailsError}
          />


      <FormControl className={classes.field}>
        <FormLabel>Note Category </FormLabel>
        <RadioGroup value={category} onChange={(e) => {setCategory(e.target.value)}}>
          <FormControlLabel control={<Radio />} label="Money" value="money"/>
          <FormControlLabel control={<Radio />} label="To Dos" value="todos"/>
          <FormControlLabel control={<Radio />} label="Reminders" value="reminders"/>
          <FormControlLabel control={<Radio />} label="Work" value="work"/>
        </RadioGroup>
      </FormControl>
    
        <Button
              className={classes.btn}
              type="submit" 
              color="secondary" 
              variant="contained"
              // startIcon={<SendIcon />}
              endIcon={<KeyboardArrowRightIcon />}>
                      Submit
        </Button>

        </form>


    </Container>

  )
}
