import React, {useState} from 'react';

import {
  BrowserRouter,
  Route,
  Routes,
} from 'react-router-dom';

import {
  createMuiTheme,
  makeStyles,
  ThemeProvider} from '@material-ui/core';
import { purple } from '@mui/material/colors';

import Auth from './Components/auth/Auth';
import Calendar from './Components/Calendar';
import Layout from './Components/Layout';
import Create from './Components/pages/Create';
import Notes from './Components/pages/Notes';

const theme = createMuiTheme({
  palette: {
    primary: {
      main: '#fefefe'
    },
    secondary: purple
  },

  typography: {
    fontFamily: 'Quicksand',

    fontWightLight: 400,

    fontWightRegular: 500,

    fontWightMedium: 600,

    fontWightBold: 700
  },
});


const useStyles = makeStyles({
  page: 
  {
    background: '#f9f9f9',
    width: '100%' 
  },

  field: {
    marginTop: 20,
    marginBotton: 20,
    display: 'block'
  }
});



const App = () => 
{
  const classes = useStyles();
  const [user, setUser] = useState("");
  const [secretData, setSecretData] = useState("");
  const [accessToken, setAccessToken] = useState("");

  const handleUserSignIn = async (userData) => {

    let isSuccess = false;

    try {
      const { data } = await apiClient.post('auth/login', {
        ...userData
      });

      localStorage.setItem('token', data.refreshToken);

      setUser(data.user.name);
      setAccessToken(data.accessToken);

      isSuccess = true;

      history.push('/');

    } catch {
      isSuccess = false;
    }

    return isSuccess;
  };

  const handleSignOut = async () => {
    try {
      await apiClient.post('auth/logout', {}, {
        headers: {
          Authorization: 'Bearer ' + accessToken
        }
      });

      setUser("");

      localStorage.removeItem('token');
      setAccessToken("");
      setSecretData("");
    } catch (err) {
      console.log(err);
      setAccessToken("");
    }
  };

  const handleUserSignUp = async (userData) => {

    let isSignUpSuccess = false;
    let message = "";

    try {
      await apiClient.post('auth/register', {
        ...userData
      });

      isSignUpSuccess = true;

    } catch (err) {
      //error.response.data.message
      const { response: { data: { message: errorMessage } } } = err;
      isSignUpSuccess = false;
      message = errorMessage
    }

    return { isSignUpSuccess, message };
  }

  const handleGetSecretData = async () => {

    try {
      const { data } = await apiClient.get('starwars/quote', {
        headers: {
          Authorization: 'Bearer ' + accessToken
        }
      });

      setSecretData(data.message);
    } catch (err) {
      setSecretData("");
    }
  }

  useEffect(() => {
    async function fetchUser() {

      const token = localStorage.getItem('token');

      setInterval(async () => {
        if (token && accessToken) {
          try {
            await apiClient.post('auth/token/refresh', {}, {
              headers: {
                Authorization: 'Bearer ' + accessToken
              }
            });
          } catch (err) {
            localStorage.removeItem('token');
            setAccessToken("");
          }
        }
      }, 30 * 60 * 1000);
    }

    fetchUser();
  }, [accessToken]);

  useEffect(() => {

    async function fetchToken() {
      const token = localStorage.getItem('token');

      if (token) {
        try {
          const { data } = await apiClient.post('auth/login/token', {
            token
          });
          localStorage.setItem('token', data.refreshToken);

          setUser(data.user.name);
          setAccessToken(data.accessToken);
        } catch (err) {
          setUser("");
          setAccessToken("");
        }
      }
    }

    fetchToken();
  }, []);
  return (
    <div>
     <ThemeProvider theme={theme} >
        <BrowserRouter>
        <Layout>
          <Routes>
                <Route path="/" element={<Auth/>}/>
                <Route path="/Create" element={<Create/>}/>
                <Route path="/Notes" element={<Notes/>}/>
                <Route path="/Calendar" element={<Calendar/>}/>
          </Routes>
        </Layout>
        </BrowserRouter>
    </ThemeProvider>   
    </div>
  )
}

export default App;
