import React, { Component } from "react";
import { connect } from "react-redux";
import axios from 'axios';

// @connect((store) => {
//   return {
//     status: store.weatherStation.status
//   }
// })

const fetchData = (region) => (dispatch) => {
  const { latitude, longitude } = region || {};

  const getDataByCity = `https://api.openweathermap.org/data/2.5/forecast?q=${region}&units=metric&appid=fbf712a5a83d7305c3cda4ca8fe7ef29`;
  const getDataByCoords = `https://api.openweathermap.org/data/2.5/forecast?lat=${latitude}&lon=${longitude}&units=metric&appid=fbf712a5a83d7305c3cda4ca8fe7ef29`;

  let location = typeof(region) === "object" ? getDataByCoords : getDataByCity;

  return axios.get(location)
    .then((response) => {
      dispatch({type:  "FETCH_DATA_FULFILLED", payload: response.data});
    })
    .catch((err) => {
      dispatch({type: "FETCH_DATA_REJECTED", payload: err}); // Error handling
    });
};

export default class Dashboard extends Component {


  _updateCity = () => {
    const city = this.__cityInput.value;
    const nesto = city.length !== 0 ? this.props.dispatch(fetchData(city)) : null;
  }

  _onkeyPress = e => {
    const hej = e.key === "Enter" ? this._updateCity() : null
  }

  render() {

    const { city, status } = this.props;
    const wrapperClass = (status === "failed") ? "weather-dashboard invalid-city" : "weather-dashboard";

    return (
      <div className={wrapperClass}>
        <header>
          <h1 className="heading">5-Day Weather Forecast</h1>
        </header>
        <section className="controls">
          <div>
            <input
              type="text"
              className="city-input"
              id="city-name"
              ref={input => {
                this.__cityInput = input;
                return this.__cityInput;
              }}
              onKeyPress={this._onkeyPress}
              placeholder={city}
            />
            <input
              type="button"
              value="&gt;"
              className="search"
              onClick={this._updateCity}
              id="change-city-btn"
            />
          </div>
        </section>
        <span className="error">Please enter valid city name!</span>
      </div>
    );
  }
}
