  const isSelected: boolean = buttonData.value === selectedView;
  const buttonClassName = `Calend__header_calendar_button${
    isSelected ? '-selected' : ''
  }`;
  const textClassName = `Calend__text Calend__header_calendar_button-text${
    isSelected ? '-selected' : ''
  }`;

  const navigateFunction = (): void => {
    if (handleClose) {
      handleClose();
    }
    setViewChanged(buttonData.value);
  };

  return (
    <ButtonBase
      className={parseClassName(buttonClassName, isMobile || isForcedMobile)}
      isDark={isDark}
      onClick={navigateFunction}
    >
      <p className={parseClassName(textClassName, isMobile, isDark)}>
        {buttonData.label}
      </p>
    </ButtonBase>
  );
};

export const isSingleView = (disabledViews?: CALENDAR_VIEW[]): boolean => {
  if (
    !disabledViews ||
    (disabledViews &&
      disabledViews?.length + 1 !== Object.values(CALENDAR_VIEW).length)
  ) {
    return false;
  }

  return true;
};

interface HeaderCalendarButtonsProps {
  disabledViews?: CALENDAR_VIEW[];
